INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`, `revision`, `cloud_id`) VALUES
(1, 'Make upload filenames lowercase', 'Makes sure that image and file uploads have lowercase filenames.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'sanitize_file_name\', \'mb_strtolower\' );', 'sample, media', 'global', 10, 0, '2024-08-07 11:17:30', 2, NULL),
(2, 'Disable admin bar', 'Turns off the WordPress admin bar for everyone except administrators.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );', 'sample, admin-bar', 'front-end', 10, 0, '2024-08-07 11:17:30', 2, NULL),
(3, 'Allow smilies', 'Allows smiley conversion in obscure places.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );', 'sample', 'global', 10, 0, '2024-08-07 11:17:30', 2, NULL),
(4, 'Current year', 'Shortcode for inserting the current year into a post or page..\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', '<?php echo date( \'Y\' ); ?>', 'sample, dates', 'content', 10, 0, '2024-08-07 11:17:30', 2, NULL),
(5, 'Delete Sort', '', 'if (!function_exists(\'custom_remove_default_sorting_storefront\')) {\n    function custom_remove_default_sorting_storefront() {\n        remove_action(\'woocommerce_before_shop_loop\', \'woocommerce_catalog_ordering\', 30);\n    }\n    add_action(\'init\', \'custom_remove_default_sorting_storefront\');\n}\n', '', 'global', 10, 1, '2024-08-07 11:19:45', 3, NULL),
(6, 'Delete Payment', '', 'function custom_checkout_js() {\n    if (is_checkout()) {\n        ?>\n        <script type=\"text/javascript\">\n            document.addEventListener(\'DOMContentLoaded\', function() {\n                var paymentOptions = document.getElementById(\'payment-method\');\n                if (paymentOptions) {\n                    paymentOptions.style.display = \'none\';\n                }\n            });\n        </script>\n        <?php\n    }\n}\nadd_action(\'wp_footer\', \'custom_checkout_js\');\n', '', 'global', 10, 1, '2024-08-07 14:15:57', 7, NULL),
(8, 'Cos', '', '// Добавление фиктивного метода оплаты\nfunction add_fake_payment_gateway($gateways) {\n    $gateways[] = \'WC_Gateway_Fake\';\n    return $gateways;\n}\nadd_filter(\'woocommerce_payment_gateways\', \'add_fake_payment_gateway\');\n\nclass WC_Gateway_Fake extends WC_Payment_Gateway {\n    public function __construct() {\n        $this->id = \'fake\';\n        $this->has_fields = false;\n        $this->method_title = \'Fake Gateway\';\n        $this->method_description = \'This is a fake gateway that does nothing.\';\n        $this->title = \'Fake Gateway\';\n        $this->init_form_fields();\n        $this->init_settings();\n\n        add_action(\'woocommerce_update_options_payment_gateways_\' . $this->id, array($this, \'process_admin_options\'));\n    }\n\n    public function init_form_fields() {\n        $this->form_fields = array(\n            \'enabled\' => array(\n                \'title\' => \'Enable/Disable\',\n                \'type\' => \'checkbox\',\n                \'label\' => \'Enable Fake Gateway\',\n                \'default\' => \'yes\'\n            ),\n            \'title\' => array(\n                \'title\' => \'Title\',\n                \'type\' => \'text\',\n                \'description\' => \'This controls the title which the user sees during checkout.\',\n                \'default\' => \'Fake Gateway\',\n                \'desc_tip\' => true,\n            ),\n        );\n    }\n\n    public function process_payment($order_id) {\n        $order = wc_get_order($order_id);\n        $order->payment_complete();\n        return array(\n            \'result\' => \'success\',\n            \'redirect\' => $this->get_return_url($order),\n        );\n    }\n}\n', '', 'global', 10, 1, '2024-08-07 14:22:15', 2, NULL);
